// MyComponent.js
import React, {useState, useEffect} from 'react';
import Draggable from 'react-draggable';
import { Resizable } from 'react-resizable';
import 'react-resizable/css/styles.css';

const TrainerSettingsWindow = () => {
    
    // Visible state
    const [isVisible, setIsVisible] = useState(true);
    
    // Size state
    const [size, setSize] = useState({ width: 200, height: 150 });
    const minConstraints = [200, 150];
    const [position, setPosition] = useState({ x: 0, y: 0 });

    // Input values states
    const [floatValue, setFloatValue] = useState(0.0);
    const [textValue, setTextValue] = useState('');
    const [isChecked, setIsChecked] = useState(false);

    useEffect(() => {
        // Load the window position from localStorage when the component mounts
        const savedPosition = JSON.parse(localStorage.getItem('trainerWindowPosition'));
        if (savedPosition) {
            setPosition(savedPosition);
        }
    }, []);

    const onResize = (event, { element, size }) => {
        setSize({ width: size.width, height: size.height });
    };
    
    const toggleVisibility = () => {
        setIsVisible(!isVisible);
    };
     
    return (
        isVisible && (
            <Draggable handle=".drag-handle">
                <Resizable width={size.width} 
                height={size.height} 
                onResize={onResize}
                minConstraints={minConstraints}>
                    <div style={{ width: size.width, height: size.height, padding: 10, 
                        backgroundColor: '#f0f0f0', position: 'relative', 
                        borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' ,
                        display: 'flex', flexDirection: 'column', overflow: 'hidden'}}>
                        
                        <div style={{ width: '100%', display: 'flex'}}>
                            <span className="drag-handle">Trainer settings</span>
                            <button onClick={toggleVisibility} style={{
                                border: 'none',
                                background: 'none',
                                cursor: 'pointer',
                                width: '35px',
                                height: 'auto',
                                display: 'flex',
                                alignItems: 'center',
                                justifyContent: 'center',
                                borderRadius: '12px',
                                backgroundColor: '#ccc',
                                //boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
                            }}>
                                <span style={{ color: 'white', fontWeight: 'bold' }}>Ã—</span>
                            </button>
                        </div>
                        <br></br>
                        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px', width: '85%'}}>
                            <label style={{ marginRight: '10px' }}>Float:</label>
                            <input 
                                type="number" 
                                step="0.1" 
                                value={floatValue} 
                                onChange={e => setFloatValue(e.target.value)}
                                style={{width:'100%'}}
                            />
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px', width: '85%' }}>
                            <label style={{ marginRight: '10px' }}>Text:</label>
                            <input 
                                type="text" 
                                value={textValue} 
                                onChange={e => setTextValue(e.target.value)}
                                style={{width:'100%'}}
                            />
                        </div>
                        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '5px', width: '85%' }}>
                            <label style={{ marginRight: '10px' }}>Checkbox:</label>
                            <input 
                                type="checkbox" 
                                checked={isChecked} 
                                onChange={e => setIsChecked(e.target.checked)}
                                style={{width:'100%'}}
                            />
                        </div>
                    </div>
                </Resizable>
            </Draggable>
        )
    );
    
};

export default TrainerSettingsWindow;
