import React, { useState, useEffect } from 'react';

function WebSocketComponent() {
  const [ws, setWs] = useState(null);
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');

  // Visible state
  const [isVisible, setIsVisible] = useState(true);
    
  // Size state
  const [size, setSize] = useState({ width: 100, height: 100 });
  const minConstraints = [100, 100];
  const [position, setPosition] = useState({ x: 0, y: 0 });

  // z-index
  const [zIndex, setZIndex] = useState(100);

    useEffect(() => {
        // Load the size and position from localStorage when the component mounts
        const savedSize = JSON.parse(localStorage.getItem('exampleWindowSize'));
        const savedPosition = JSON.parse(localStorage.getItem('exampleWindowPosition'));

        if (savedSize) {
        setSize(savedSize);
        }
        if (savedPosition) {
        setPosition(savedPosition);
        }

        // Create WebSocket connection.
        const socket = new WebSocket('ws://localhost:6789');
        
        // Connection opened
        socket.addEventListener('open', function (event) {
            console.log('Connected to WS Server');
        });

        // Listen for messages
        socket.addEventListener('message', function (event) {
            console.log('Message from server ', event.data);
            setMessages(prev => [...prev, event.data]);
        });

        // Update the state to hold WebSocket reference
        setWs(socket);

        // Cleanup on unmount
        return () => {
        socket.close();
        };
    }, []);

    const handleFocus = () => {
        const newZIndex = bringToFront();
        setZIndex(newZIndex);
    };
  // Function to send message to server
  const sendMessage = () => {
    if (ws) {
      ws.send(inputMessage);
      setInputMessage(''); // Reset input field after send
    }
  };

  return (
    <div>
      <input
        value={inputMessage}
        onChange={(e) => setInputMessage(e.target.value)}
        placeholder="Type a message..."
      />
      <button onClick={sendMessage}>Send</button>
      <div>
        <h2>Messages</h2>
        {messages.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>
    </div>
  );
}

export default WebSocketComponent;
