// WebSocketContext.js
import React, { createContext, useContext, useState, useEffect, useRef } from 'react';

const WebSocketContext = createContext(null);

export const useWebSocket = () => useContext(WebSocketContext);

export const WebSocketProvider = ({ children }) => {
  const [ws, setWs] = useState(null);
  
  const subscribersRef = useRef([]);

  useEffect(() => {
    const socket = new WebSocket('ws://localhost:6789');

    socket.addEventListener('open', function (event) {
      console.log('Connected to WS Server');
    });

    socket.onmessage = (event) => {
      const message = JSON.parse(event.data);
      // Notify all subscribers whose filter matches the message
      subscribersRef.current.forEach(({ filter, callback }) => {
        if (filter(message)) {
          callback(message);
        }
      });
    };

    setWs(socket);

    return () => {
      socket.close();
    };
  }, []);

  const subscribe = (filter, callback) => {
    const subscriber = { filter, callback };
    subscribersRef.current.push(subscriber);
    // Return an unsubscribe function
    return () => {
      subscribersRef.current = subscribersRef.current.filter(sub => sub !== subscriber);
    };
  };

  // Function to send messages to the server
  const send = (message) => {
    if (ws && ws.readyState === WebSocket.OPEN) {
      ws.send(JSON.stringify(message));
    } else {
      console.error('WebSocket is not connected.');
    }
  };

  return (
    <WebSocketContext.Provider value={{ ws, subscribe, send }}>
      {children}
    </WebSocketContext.Provider>
  );
};
