// MyComponent.js
import React, {useRef, useEffect, useState} from 'react';
import { useWebSocket } from './WebSocketContext';
import 'react-resizable/css/styles.css';

const CanvasComponent = () => {
    const canvasRef = useRef(null);
    const [imageSrc, setImageSrc] = useState('');

    // Websocket setup (from global context)
    const { subscribe, send } = useWebSocket();
    

    const sendTestData = () => {
        const message = {
          type: 'customType', // Define the message type or structure as needed
          data: "test",
        };
        send(message); // Use the send function from the context
      };
      useEffect(() => {
        // Setup canvas
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');
        // Clear canvas for simplicity
        context.clearRect(0, 0, canvas.width, canvas.height);
        context.fillStyle = "#fff0f0"; // Replace with any color you like
        context.fillRect(0, 0, canvas.width, canvas.height);
      }, []);

    useEffect(() => {        
        // Define the message filter and callback for subscription
        const messageFilter = (message) => message.type === 'image';
        const messageCallback = (message) => {
            /* Do something with message/message.data */
            console.log(message);
            const imageBase64 = message.image;
            const imageSrc = `data:image/jpeg;base64,${imageBase64}`;
            setImageSrc(imageSrc);  
        };

        // Subscribe to messages that pass the filter
        const unsubscribe = subscribe(messageFilter, messageCallback);

        // Unsubscribe from messages when the component unmounts
        return () => unsubscribe();
    }, [subscribe]);

    useEffect(() => {
        if (imageSrc && canvasRef.current) {
          const canvas = canvasRef.current;
          const ctx = canvas.getContext('2d');
          
          // Create a new image and set its source to the Base64 string
          const image = new Image();
          image.src = `data:image/jpeg;base64,${imageSrc}`;
      
          // Once the image has loaded, draw it on the canvas
          image.onload = () => {
            // Resize canvas to match image dimensions
            canvas.width = image.width;
            canvas.height = image.height;
      
            // Draw the image onto the canvas
            ctx.drawImage(image, 0, 0);
          };
        }
      }, [imageSrc]); // Re-run the effect when imageSrc changes

    return (
        <div className="canvas-container">
          <canvas ref={canvasRef}  />
        </div>
    );
    
};

export default CanvasComponent;
