import React, { useRef, useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaintBrush, faEraser, faFont } from '@fortawesome/free-solid-svg-icons';
import './App.css';

function App() {
  const canvasRef = useRef(null);
  const [selectedTool, setSelectedTool] = useState('');
  const [inputValues, setInputValues] = useState({ input1: 0.0, input2: 0.0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    // Clear canvas for simplicity
    context.clearRect(0, 0, canvas.width, canvas.height);
  }, [selectedTool]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputValues((prevValues) => ({ ...prevValues, [name]: value }));
  };

  const DropdownMenu = ({ title, items }) => {
    const [isOpen, setIsOpen] = useState(false);

    return (
      <div className="menu-item"
           onMouseEnter={() => setIsOpen(true)}
           onMouseLeave={() => setIsOpen(false)}>
        {title}
        {isOpen && (
          <div className="dropdown-content">
            {items.map((item, index) => (
              <button key={index}>{item}</button>
            ))}
          </div>
        )}
      </div>
    );
  };

  function MenuBar() {
    return (
      <div className="menu-bar">
        <DropdownMenu title="File" items={['New', 'Open', 'Save']}/>
        <DropdownMenu title="Edit" items={['Undo', 'Redo']}/>
        {/* Add more dropdown menus as needed */}
      </div>
    );
  }

  const tools = [
    { id: 'brush', icon: faPaintBrush, name: 'Brush' },
    { id: 'eraser', icon: faEraser, name: 'Eraser' },
    { id: 'text', icon: faFont, name: 'Text' },
  ];

  const handleToolClick = (toolId) => {
    setSelectedTool(toolId);
    // You can extend this method to change canvas behavior based on the selected tool
  };

  return (
    <div className="App">
      <MenuBar />
      <Draggable>
        <div className="toolbar">
          {tools.map((tool) => (
            <div key={tool.id} className={`tool-icon ${selectedTool === tool.id ? 'selected' : ''}`} onClick={() => handleToolClick(tool.id)}>
              <FontAwesomeIcon icon={tool.icon} title={tool.name} />
            </div>
          ))}
        </div>
      </Draggable>
      <Draggable>
        <div className="input-window">
          <div className="input-group">
            <label htmlFor="input1">Input 1:</label>
            <input type="number" step="0.1" name="input1" id="input1" value={inputValues.input1} onChange={handleInputChange} />
          </div>
          <div className="input-group">
            <label htmlFor="input2">Input 2:</label>
            <input type="number" step="0.1" name="input2" id="input2" value={inputValues.input2} onChange={handleInputChange} />
          </div>
        </div>
      </Draggable>
      <div className="canvas-container">
        <canvas ref={canvasRef} width={600} height={400} />
      </div>
      <div className="tool-indicator">Selected Tool: {selectedTool}</div>
    </div>
  );
}

export default App;
