import React, { useRef, useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaintBrush, faEraser, faFont } from '@fortawesome/free-solid-svg-icons';
import './App.css';

function App() {
  const canvasRef = useRef(null);
  const [selectedTool, setSelectedTool] = useState('');
  const [inputValues, setInputValues] = useState({ input1: 0.0, input2: 0.0 });

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext('2d');
    // Clear canvas for simplicity
    context.clearRect(0, 0, canvas.width, canvas.height);
    context.fillStyle = "#f0f0f0"; // Replace with any color you like
    context.fillRect(0, 0, canvas.width, canvas.height);
  }, [selectedTool]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setInputValues((prevValues) => ({ ...prevValues, [name]: value }));
  };

  const tools = [
    { id: 'brush', icon: faPaintBrush, name: 'Brush' },
    { id: 'eraser', icon: faEraser, name: 'Eraser' },
    { id: 'text', icon: faFont, name: 'Text' },
  ];

  const handleToolClick = (toolId) => {
    setSelectedTool(toolId);
    // You can extend this method to change canvas behavior based on the selected tool
  };

  const SubMenu = ({ items }) => {
    return (
      <div className="submenu">
        {items.map((item, index) => (
          <a key={index} href="#!" onClick={item.callback}>
            {item.name}
          </a>
        ))}
      </div>
    );
  };

  const MenuItem = ({ title, items }) => {
    const [isSubMenuVisible, setIsSubMenuVisible] = useState(false);

    return (
      <div className="menu-item"
           onMouseEnter={() => setIsSubMenuVisible(true)}
           onMouseLeave={() => setIsSubMenuVisible(false)}>
        {title}
        {isSubMenuVisible && <SubMenu items={items} />}
      </div>
    );
  };

  const SettingsMenu = () => {
    const [isMenuVisible, setIsMenuVisible] = useState(false);

    const fileItems = [
      { name: 'New', callback: () => console.log('New File') },
      { name: 'Open', callback: () => console.log('Open File') },
      { name: 'Save', callback: () => console.log('Save File') },
    ];

    const editItems = [
      { name: 'Undo', callback: () => console.log('Undo') },
      { name: 'Redo', callback: () => console.log('Redo') },
    ];

    return (
      <div className="settings-container">
        <button className="settings-button" onClick={() => setIsMenuVisible(!isMenuVisible)}>
          ☰ {/* This is a simple representation of a settings icon */}
        </button>
        {isMenuVisible && (
          <div className="menu">
            <MenuItem title="File" items={fileItems} />
            <MenuItem title="Edit" items={editItems} />
            {/* Additional menu items can be added here */}
          </div>
        )}
      </div>
    );
  };

  function DraggableBox() {
    const [isVisible, setIsVisible] = useState(true);

    return (
      <>
        {isVisible && (
          <Draggable>
            <div style={{ width: 200, height: 100, padding: 10, backgroundColor: '#f0f0f0', cursor: 'move', position: 'relative', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
              <button className="close-btn" onClick={() => setIsVisible(false)} style={{
                position: 'absolute',
                top: '10px',
                right: '10px',
                border: 'none',
                background: 'none',
                cursor: 'pointer',
                width: '24px',
                height: '24px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                borderRadius: '12px',
                backgroundColor: '#ccc',
                boxShadow: '0 2px 4px rgba(0,0,0,0.2)'
              }}>
                <span style={{ color: 'white', fontWeight: 'bold' }}>×</span>
              </button>
              <p>Drag me around!</p>
            </div>
          </Draggable>
        )}
        {!isVisible && <button onClick={() => setIsVisible(true)}>Show</button>}
      </>
    );
  }

  return (
    <div className="App">
      <SettingsMenu />
      <DraggableBox />
      <Draggable>
        <div className="toolbar">
          {tools.map((tool) => (
            <div key={tool.id} className={`tool-icon ${selectedTool === tool.id ? 'selected' : ''}`} onClick={() => handleToolClick(tool.id)}>
              <FontAwesomeIcon icon={tool.icon} title={tool.name} />
            </div>
          ))}
        </div>
      </Draggable>
      <Draggable>
        <div className="input-window">
          <div className="input-group">
            <label htmlFor="input1">Input 1:</label>
            <input type="number" step="0.1" name="input1" id="input1" value={inputValues.input1} onChange={handleInputChange} />
          </div>
          <div className="input-group">
            <label htmlFor="input2">Input 2:</label>
            <input type="number" step="0.1" name="input2" id="input2" value={inputValues.input2} onChange={handleInputChange} />
          </div>
        </div>
      </Draggable>
      <div className="canvas-container">
        <canvas ref={canvasRef} width={600} height={400} />
      </div>
      <div className="tool-indicator">Selected Tool: {selectedTool}</div>
    </div>
  );
}

export default App;
